  {id: 18676, name: "Sonic Blast", deck: "A 2D Side Scroller using pre-rendered graphics (similar to Sonic 3D Blast on the Genesis).", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/1870228-box_sonicb.png", created_at: "2015-06-23 06:02:29", updated_at: "2015-06-23 06:02:29", release_date: "1996-12-12", platforms: "Game Gear@@@Sega Master System@@@Nintendo 3DS eShop@@@"},
  {id: 14433, name: "Sonic CD", deck: "Sonic the Hedgehog's sole adventure for the Sega CD, as he travels through time to save the fate of Little Planet from Dr. Robotnik's army of robots (including Sonic's new adversary: his metallic doppelganger).", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/0/1486/2078290-soniccd_brighterandcleaner.png", created_at: "2015-06-23 06:03:25", updated_at: "2015-06-23 06:03:25", release_date: "1993-09-23", platforms: "Sega CD@@@Xbox 360 Games Store@@@PlayStation Network (PS3)@@@PC@@@iPhone@@@iPad@@@Android@@@Windows Phone@@@Ouya@@@"},
  {id: 7901, name: "Mega Man Legends", deck: "Mega Man's legendary transition to 3D was notable for its new action-RPG like approach and anime-styled storyline.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/16/164734/2299255-529738_44502_front.jpg", created_at: "2015-06-23 06:03:35", updated_at: "2015-06-23 06:03:35", release_date: "1998-08-31", platforms: "PlayStation Portable@@@PlayStation@@@Nintendo 64@@@PC@@@"},
  {id: 19060, name: "Sonic Adventure 2", deck: "Sonic Adventure 2 is a 2001 sequel to Sonic Adventure. It marks the debut appearance of Shadow the Hedgehog and Rouge the Bat, and is the first ever Sonic title where Dr. Eggman is playable.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/1803647-box_sonicad2.png", created_at: "2015-06-23 06:03:46", updated_at: "2015-06-23 06:03:46", release_date: "2001-06-05", platforms: "GameCube@@@Dreamcast@@@Xbox 360 Games Store@@@PlayStation Network (PS3)@@@PC@@@"},
  {id: 5251, name: "Mega Man", deck: "Featuring a robotic protagonist that could siphon the abilities of his adversaries, Mega Man is a side-scrolling platformer that begins the never-ending struggle between cheeky hero Mega Man and the evil roboticist Dr. Wily.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/9/93770/2362042-nes_megaman1.jpg", created_at: "2015-06-23 06:03:53", updated_at: "2015-06-23 06:03:53", release_date: "1987-12-17", platforms: "Nintendo Entertainment System@@@PlayStation@@@Wii Shop@@@PlayStation Network (PS3)@@@PlayStation Network (PSP)@@@Nintendo 3DS eShop@@@Wii U@@@PlayStation Network (Vita)@@@"},
  {id: 8238, name: "Mega Man Battle Network", deck: "MegaMan Battle Network is the first of the MegaMan Battle Network series, Where MegaMan is controlled though a cybernetic universe", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/1806078-box_mmbn.png", created_at: "2015-06-23 06:06:53", updated_at: "2015-06-23 06:06:53", release_date: "2001-03-21", platforms: "Game Boy Advance@@@Wii U@@@"},
  {id: 29264, name: "Mega Man III", deck: "The third handheld installment of the Mega Man series has him (along with his dog Rush and helper robot Eddie) fighting eight more revived Robot Masters (from Mega Man 3 and Mega Man 4), while finding the whereabouts of Dr. Wily (who has taken control of an oil platform to power his latest weapon against the hero).", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/1806088-box_mmgb3.png", created_at: "2015-06-23 06:07:07", updated_at: "2015-06-23 06:07:07", release_date: "1992-12-11", platforms: "Game Boy@@@Nintendo 3DS eShop@@@"},
  {id: 7110, name: "Halo 2", deck: "The sequel to Halo: Combat Evolved continues the epic story of humanity's last and best hope: the super-soldier Master Chief, as he defends Earth from a Covenant invasion and fights to prevent the activation of another Halo ring.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2079825-box_halo2.png", created_at: "2015-06-23 06:08:55", updated_at: "2015-06-23 06:08:55", release_date: "2004-11-09", platforms: "Xbox@@@PC@@@"},
  {id: 26786, name: "Halo: Reach", deck: "Halo: Reach is the prequel to Halo: Combat Evolved, and chronicles one of the most cataclysmic events of the Halo Universe, the destruction of the UNSC fortress world Reach, through the eyes of a squad of Spartan super-soldiers known as Noble Team.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/0/5150/1331572-reach.jpg", created_at: "2015-06-23 06:09:02", updated_at: "2015-06-23 06:09:02", release_date: "2010-09-14", platforms: "Xbox 360@@@Xbox 360 Games Store@@@"},
  {id: 35533, name: "Halo 4", deck: "The first game in the second trilogy of the Halo sci-fi series has the Master Chief awakening from cryostasis (four years after the events of the previous trilogy) as he explores the mysterious Forerunner shield world Requiem, fights a newly-formed Covenant group, and accidentally awakens an ancient evil.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2348782-box_halo4.png", created_at: "2015-06-23 06:09:08", updated_at: "2015-06-23 06:09:08", release_date: "2012-11-05", platforms: "Xbox 360@@@Xbox 360 Games Store@@@"},
  {id: 24035, name: "Halo 3: ODST", deck: "Set during the events of Halo 2, Halo 3: ODST puts players as a silent Orbital Drop Shock Trooper (known as the Rookie) as he traverses through the Covenant-occupied metropolis of New Mombasa to find the whereabouts of his squadmates.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2079827-box_haloodst.png", created_at: "2015-06-23 06:09:31", updated_at: "2015-06-23 06:09:31", release_date: "2009-09-22", platforms: "Xbox 360@@@Xbox 360 Games Store@@@"},
  {id: 29413, name: "Mega Man 10", deck: "When a robotic virus causes robots to either malfunction or go berserk, it's up to Mega Man and Proto Man to help cure it in the second deliberately-retro installment of the Mega Man series.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/0/112/1294866-front2.jpg", created_at: "2015-06-23 06:10:03", updated_at: "2015-06-23 06:10:03", release_date: "2010-03-01", platforms: "Xbox 360 Games Store@@@Wii Shop@@@PlayStation Network (PS3)@@@"},
  {id: 7391, name: "Mega Man 7", deck: "The seventh installment of the Mega Man series brings Mega Man's antics to Nintendo's 16-bit console, as our robotic hero sets out to find the whereabouts of Dr. Wily (who recently escaped from prison) while encountering a mysterious new robotic ally Bass.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2593785-box_mm7b.png", created_at: "2015-06-23 06:10:27", updated_at: "2015-06-23 06:10:27", release_date: "1995-03-15", platforms: "Super Nintendo Entertainment System@@@Wii U@@@"},
  {id: 7481, name: "Paper Mario: The Thousand-Year Door", deck: "In his second paper-themed adventure, Mario must once again rescue the captured princess while he foils the X-Nauts' plans. The Thousand-Year Door features a turn-based battle system with real-time elements.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2108509-box_pmttyd.png", created_at: "2015-06-23 06:11:50", updated_at: "2015-06-23 06:11:50", release_date: "2004-10-11", platforms: "GameCube@@@"},
  {id: 14425, name: "Super Mario Land 2: 6 Golden Coins", deck: "While Mario was away in Sarasa Land, Wario sweeps in and puts the whole of Mario Land under a magic spell renaming it Wario Land. It's up to Mario to come back and save the day again!", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/2/26763/1125987-box_scan030.jpg", created_at: "2015-06-23 06:13:24", updated_at: "2015-06-23 06:13:24", release_date: "1992-11-02", platforms: "Game Boy@@@Nintendo 3DS eShop@@@"},
  {id: 19596, name: "Tomb Raider III: Adventures of Lara Croft", deck: "The third game in the Tomb Raider series. Lara travels the world to find four meteorite stones.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/1810972-box_traider3.png", created_at: "2015-06-23 06:18:09", updated_at: "2015-06-23 06:18:09", release_date: "1998-11-21", platforms: "Mac@@@PlayStation@@@PC@@@"},
  {id: 9306, name: "Mario & Luigi: Superstar Saga", deck: "Mario &amp; Luigi: Superstar Saga is an RPG for the Game Boy Advance featuring the famous brothers in an adventure to save both the Mushroom and Beanbean Kingdoms. The game is well-known for its comedy and colorful graphics.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2108507-box_malss.png", created_at: "2015-06-23 06:24:05", updated_at: "2015-06-23 06:24:05", release_date: "2003-11-17", platforms: "Game Boy Advance@@@Wii U@@@"},
  {id: 16094, name: "Super Mario Galaxy", deck: "In Mario's first Wii adventure, the famed plumber travels throughout the universe on his latest quest to save Princess Peach from the evil Bowser.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/1/13774/488943-super_mario_galaxy.jpg", created_at: "2015-06-23 06:25:00", updated_at: "2015-06-23 06:25:00", release_date: "2007-11-01", platforms: "Wii@@@Wii U@@@"},
  {id: 46251, name: "Pokémon Omega Ruby/Alpha Sapphire", deck: "A new Pokémon adventure for Nintendo 3DS and 2DS. Omega Ruby and Alpha Sapphire are remakes of Pokémon Ruby and Sapphire, respectively.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/82063/2630130-rubysapphire.jpg", created_at: "2015-06-23 06:25:45", updated_at: "2015-06-23 06:25:45", release_date: "2014-11-21", platforms: "Nintendo 3DS@@@Nintendo 3DS eShop@@@"},
  {id: 16948, name: "Mega Man X3", deck: "The third installment of the Mega Man X franchise has both Mega Man X and the rebuilt Zero (playable for the first time) hunting down the Maverick scientist Dr. Doppler, who has congregated the reformed Mavericks for a more sinister purpose.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2586375-box_mmx3.png", created_at: "2015-06-23 06:27:26", updated_at: "2015-06-23 06:27:26", release_date: "1995-12-01", platforms: "Super Nintendo Entertainment System@@@PlayStation@@@Saturn@@@PC@@@Wii U@@@"},
  {id: 6712, name: "Metal Gear", deck: "Known as the first \"stealth action\" game, Metal Gear tells the tale of \"Solid Snake\", a rookie operative of the U.S. special forces unit FOXHOUND, as he infiltrates a fortified military compound (known as Outer Heaven) to destroy a mysterious superweapon known as \"Metal Gear\"", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2457095-box_mg.png", created_at: "2015-06-23 06:33:10", updated_at: "2015-06-23 06:33:10", release_date: "1987-07-07", platforms: "Commodore 64@@@MSX@@@Nintendo Entertainment System@@@Wii Shop@@@PC@@@"},
  {id: 6073, name: "Mario Tennis", deck: "The second Mario sports game developed by Camelot.  It was the first game to include Waluigi who eventually became a recurring character in Mario games.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2160627-box_mt.png", created_at: "2015-06-23 06:33:37", updated_at: "2015-06-23 06:33:37", release_date: "2000-08-28", platforms: "Nintendo 64@@@Wii Shop@@@"},
  {id: 19929, name: "Paper Mario", deck: "The second Mario themed RPG, Bowser has stolen the Star Rod and has now has near limitless power. Mario goes on a quest to save the Seven Star Spirits, and retrieve the Star Rod from Bowser in order to save Princess Peach and the Mushroom Kingdom.", score: nil, thumbnail_url: nil, boxart_url: "http://static.giantbomb.com/uploads/scale_small/8/87790/2108508-box_pm.png", created_at: "2015-06-23 06:33:56", updated_at: "2015-06-23 06:33:56", release_date: "2000-08-11", platforms: "Nintendo 64@@@Wii Shop@@@Wii U@@@"}
])
